        public static TextObject npc_starts_confrontation_surprised = new("{=Dramalord482}Oh, hello {TITLE}. I... didn't expect you...");

        public static TextObject player_interaction_kick_clan = new("{=Dramalord497}You have shown that you do not belong in this clan. Gather your things and leave at once.");
        public static TextObject player_interaction_kick_kingdom = new("{=Dramalord498}There is no place for your clan in my kingdom. Depart now, and do not return!");
        public static TextObject npc_interaction_kick_friend = new("{=Dramalord499}So, this is what our friendship amounts to in the end? What a disappointment. Very well, I will do as you ask.");

        public static TextObject player_interaction_abort_own = new("{=Dramalord502}I want you to get rid of our unborn child.");
        public static TextObject player_interaction_abort_self = new("{=Dramalord504}I do not want to give birth to our child.");
        public static TextObject player_interaction_abort_other = new("{=Dramalord503}I want you to get rid of the unborn child of you and {HERO}.");


        public static TextObject npc_interaction_abort_own_accept = new("{=Dramalord507}If you consider this necessary, then I will take care of it, {TITLE}.");
        public static TextObject npc_interaction_abort_own_decline = new("{=Dramalord508}I will not accept this, {TITLE}. I urge you to reconsider, or there will be consequences!");
        public static TextObject npc_interaction_abort_self_accept = new("{=Dramalord509}If that is your wish, then so be it, {TITLE}. I cannot force you to keep it.");
        public static TextObject npc_interaction_abort_self_decline = new("{=Dramalord510}Are you insane, {TITLE}? This child is the manifestation of our love!");
        public static TextObject npc_interaction_abort_other_accept = new("{=Dramalord511}You are right. It would not be proper to give birth to a child of {HERO}.");
        public static TextObject npc_interaction_abort_other_decline = new("{=Dramalord512}I cannot, {TITLE}! This child is the result of the love I have for {HERO}!");

        public static TextObject npc_gossip_bethrothed = new("{=Dramalord532}Have you heard? {HERO} and {OTHER} are to be wed—who would have thought, after all those whispers about their courtly dalliances?");
        public static TextObject npc_gossip_married = new("{=Dramalord533}Did you hear? {HERO} and {OTHER} are now husband and wife—though with all that has been said, one wonders if vows alone will silence the rumors.");
        public static TextObject npc_gossip_date = new("{=Dramalord534}Word travels fast—{HERO} and {OTHER} have been meeting in secret, though if they wish to remain unnoticed, they should be less obvious with their stolen glances.");
        public static TextObject npc_gossip_sex = new("{=Dramalord535}Well, the whole castle is aflutter! {HERO} and {OTHER} were found abed together, and not even the dawn could wash away the scandal.");
        public static TextObject npc_gossip_child = new("{=Dramalord536}Whispers abound—{HERO} and {OTHER} have welcomed a child, though some say the timing is rather... convenient.");

        public static TextObject npc_confrontation_reply_gossip = new("{=Dramalord538}Pay no mind to the chatter, {TITLE}. It is but idle gossip, and soon enough, it will be forgotten.");
        public static TextObject npc_confrontation_gossip_accept = new("{=Dramalord539}Perhaps you are right, {TITLE}. Soon enough, another name will be on their lips, and this will be but a passing whisper.");

        public static TextObject player_quest_joinparty_start = new("{=Dramalord545}Tell me, {TITLE}, would you like to join my party for a while?");
        public static TextObject player_quest_joinparty_end = new("{=Dramalord546}These past days by your side have been a gift, {TITLE}, and I cherish every moment we have shared. Though we must part for now, I have no doubt our paths will cross again soon.");

        public static TextObject player_quest_marriage_start = new("{=Dramalord547}If you truly wish to make me yours, then you must seek {HERO}'s blessing. My heart is already yours, but it is their permission that will grant us the future we both desire.");
        public static TextObject player_quest_marriage_ask = new("{=Dramalord550}{TITLE}, I humbly seek your blessing to take the hand of {HERO} in marriage.");
        public static TextObject player_quest_marriage_agree = new("{=Dramalord551}You have shown yourself to be honorable and true, and I see the love {HERO} holds for you in her eyes. With that, I give my blessing. May your marriage be strong, your days joyful, and your hearts ever steadfast.");
        public static TextObject player_quest_marriage_later = new("{=Dramalord552}You speak admirably, but you are still yet unproven. Earn your place, show me your strength and honor, and then we shall speak of marriage.");
        public static TextObject player_quest_marriage_decline = new("{=Dramalord553}You? Marry my daughter? A lowly wretch with little to offer? {HERO} is to wed someone of standing, not some starry-eyed fool who thinks love alone will keep a household.");

        public static TextObject npc_interaction_betrothed_married = new("{=Dramalord555}But {TITLE}, you know that I am already married...");

        public static TextObject player_interaction_duel = new("{=Dramalord581}{TITLE}! I challenge you for the love of {HERO}!");
        public static TextObject npc_interaction_duel = new("{=Dramalord582}Very well. Draw your weapon!");
